import requests
from bs4 import BeautifulSoup
import textwrap

def gen(url):
	global count
	r=requests.get(url).text
	soup=BeautifulSoup(r,'html.parser')
	ls=soup.find_all('p',attrs={'class':'type'})
	d=[]
	for i in ls:
		s=str(i.text).replace("\n","")
		d.append(s)
	for i in d:
		print(textwrap.fill(i,80))
		print()
	el=soup.find("div",attrs={'class':'anime_video_body'}).find_all("a")
	temp=[]
	for i in el:
		temp.append(int(i.get('ep_end')))
	count=max(temp)
	print("Total Episodes: "+str(count))
	print()
count=0