import requests
from bs4 import BeautifulSoup
from prettytable import PrettyTable

dic={}
animename={}

def get_data(name):
	r=requests.Session()
	search_url="https://gogo-stream.com/search.html?keyword="+name
	search_res=r.get(search_url).text
	soup=BeautifulSoup(search_res,'html.parser').find_all('li',attrs={'class':'video-block'})
	t=PrettyTable(['Code','Anime','Genre'])
	url="https://gogo-play.net/"
	k=0
	for i in soup:
		k+=1
		ep_url=url+str(i.find('a').get('href'))
		name=str(i.find('img').get('alt'))
		if "Movie" in name:
			t.add_row([k,name,"Movie"])
		elif "OVA" in name or "Specials" in name:
			t.add_row([k,name,"OVA/Specials"])
		else:
			t.add_row([k,name,"Tv-Series"])
		dic[k]=ep_url
		animename[k]=name
	t.align="l"
	print(t)
