import requests
from bs4 import BeautifulSoup
import textwrap

def gen(url):
	global count
	r=requests.get(url).text
	soup=BeautifulSoup(r,'html.parser').find('div',attrs={'class':'video-details'}).find_all('div')
	for i in soup:
		print(textwrap.fill(i.text,80))
	ind=url.index("-episode-")
	print("\nEpisodes: "+url[ind+9:]+"\n")
	count=int(url[ind+9:])
count=0